import{D as p}from"./DefaultHeader-4e7cd0f2.js";import{f as u,r as g,o as t,c as o,b as m,w as f,a as e,t as d,g as b,u as l,F as c,h,i as x,d as v,j as y}from"./index-c05384aa.js";import{u as T}from"./products-051758dc.js";import"./MobileHeader-2d4f26ea.js";const _={class:"max-h-[250px] overflow-hidden"},k=["src"],w={class:"bg-white px-[18px] py-[9px] text-custom-green text-center"},V={class:"text-xl font-bold"},C=u({__name:"ProductItem",props:{product:null},setup(s){return(a,i)=>{const r=g("router-link");return t(),o("li",null,[m(r,{to:{name:"products.show",params:{product:s.product.id}}},{default:f(()=>[e("div",_,[e("img",{alt:"tomatoes-in-hand",class:"bg-no-repeat bg-cover bg-center",src:s.product.image},null,8,k)]),e("div",w,[e("h5",V,d(s.product.title),1),e("p",null,d(s.product.short_text),1)])]),_:1},8,["to"])])}}}),L={class:"bg-cyan-50 bg-opacity-30 pb-[50px]"},F=v('<header class="w-full h-[320px] lg:h-[480px] bg-no-repeat bg-cover bg-center" style="background-image:url(/images/onion-banner.jpeg);"></header><section class="xl:container xl:mx-auto mx-2.5 pt-[51px]"><div class="pb-10 flex items-center flex-col xl:block"><h3 class="text-[44px] text-center lg:text-start text-custom-gray-900 font-bold"> Agridera Fresh Market Onion Hybrids </h3><div class="pt-10 text-custom-green hidden lg:block text-[20px]">*****</div></div><div class="text-[17px] space-y-6 text-custom-gray-900"><p> Agridera aims to be one of the world&#39;s leading companies in the breeding, production, and marketing of tomato seeds. <span class="font-bold"> We invest intensively in research and development to provide solutions to growers and consumers in numerous countries around the world. </span> In particular, we breed improved disease-resistant cultivars adapted to different markets globally. </p><p> Currently, emphasis is placed on combining Tomato Yellow Leaf Curl Virus (TYLCV) and Tomato Spotted Wilt Virus (TSWV) resistances with other soil borne diseases into superior tomato breeding lines and hybrids. Moreover, comprehensive efforts are being undertaken to develop varieties resistance to TBRFV virus (Tomato Brown Rugose Fruit Virus). </p><p> In terms of horticultural traits, breeding is for earliness, different fruit sizes, firmness, red fruit color, flavor, crack resistance, shelf life, and high/low temperature fruit set. Combinations of quality features and disease resistance are sought in a variety of types, namely round and elongated shapes. </p><div class="pl-[28px] rounded-sm relative"><p class="before:h-full before:bg-custom-red-700 before:absolute before:left-0 before:top-0 before:w-1 before:rounded-md"> Agirdera Tomato breeding programs are focusing on three main segments: Greenhouse Indeterminate Tomatoes, Open-field Indeterminate Tomatoes, and Bush Type Open-field Tomatoes. </p></div></div></section><section class="container mx-auto my-10"><ul class="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-[30px] bg-cyan-50 bg-opacity-75 py-[50px] px-[22px]"><li class="text-custom-gray-900"><h5 class="text-xl font-bold"> Greenhouse Tomato Breeding Program </h5><div class="border-b border-custom-gray-900/75 my-[15px]"></div><p><span class="font-bold"> This breeding is aimed for the Spanish and Turkish markets.</span> For single and cluster picks, round fruit shape and resistances of TYLCV, TSWV, Nematodes and FORL (Fusarium oxysporum f.sp. radicis-lycopersici). Some hybrids may include more resistances, such as Cladosporium and Leveillula Taurica (Lv). </p></li><li class="text-custom-gray-900"><h5 class="text-xl font-bold max-w-[300px]"> Open-field Indeterminate Tomatoes </h5><div class="border-b border-custom-gray-900/75 my-[15px]"></div><p><span class="font-bold"> This breeding is for Brazilian, Moroccan, and Turkish markets.</span> The main focus is on large round shape fruits (200 grams plus); and resistances of TYLCV, TSWV, Nematodes and FORL. In the near future, F3 (Fusarium oxysporum f.sp. lycopersici) will be included as basic resistance in our cultivars. </p></li><li class="text-custom-gray-900"><h5 class="text-xl font-bold max-w-[300px]"> Open-field Bush Type Tomatoes </h5><div class="border-b border-custom-gray-900/75 my-[15px]"></div><p><span class="font-bold"> This breeding is for Mediterranean markets.</span> We breed round and elongated shapes, standard sizes (140- 180 grams), and beef sizes (min. 180 grams up to 300 grams and more). We combine the resistances of TYLCV, TSWV and Nematodes as basic resistances in our hybrids. However, some hybrids may include more resistances, such as Cladosporium and Leveillula Taurica (Lv). </p></li></ul></section><div class="lg:container lg:mx-auto mx-2.5 mb-10"><div class="pl-[28px] rounded-sm relative"><p class="before:h-full before:bg-custom-red-700 before:absolute before:left-0 before:top-0 before:w-1 before:rounded-md text-custom-gray-900 font-bold"> Agridera operates through international and local distributers around the world. </p></div></div>',4),B={class:"lg:container lg:mx-auto mx-2.5"},S={key:0,class:"grid grid-cols-1 justify-items-center sm:grid-cols-2 md:grid-cols-3 gap-[30px]"},Y=u({__name:"IndexProducts",setup(s){const{getProducts:a,products:i,isProductsSet:r}=T();return b(a),(I,W)=>(t(),o(c,null,[m(p),e("main",L,[F,e("section",B,[l(r)?(t(),o("ul",S,[(t(!0),o(c,null,h(l(i),n=>(t(),y(C,{key:n.id,product:n},null,8,["product"]))),128))])):x("",!0)])])],64))}});export{Y as default};
